import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

function Home() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    async function fetchUser() {
      const token = localStorage.getItem('token');
      if (!token) return;

      try {
        const res = await fetch('/whoami', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        if (res.ok) {
          const data = await res.json();
          setUser(data);
        } else {
          localStorage.removeItem('token'); // Clear invalid token
        }
      } catch (err) {
        console.error('Error fetching user:', err);
      }
    }

    fetchUser();
  }, []);

  return (
    <div>
      <h1>üè® Welcome to Ch√¢teau Hotel</h1>

      {user ? (
        <div>
          <p>Hello, <strong>{user.username}</strong> üëã</p>
          {user.is_admin && <p>You are logged in as <strong>Admin</strong>.</p>}
        </div>
      ) : (
        <div>
          <p>Please <Link to="/login">Login</Link> or <Link to="/register">Register</Link></p>
        </div>
      )}

      <p>
        <Link to="/rooms">View Available Rooms</Link>
      </p>
    </div>
  );
}

export default Home;
