import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

function Home() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) return;

    fetch('/whoami', {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then((res) => res.json())
      .then((data) => {
        if (data && data.username) setUser(data);
      });
  }, []);

  return (
    <div>
      <h2>Welcome to Ch√¢teau Hotel</h2>
      {user ? (
        <p>Hello, {user.username}!</p>
      ) : (
        <>
          <p>Please <Link to="/login">login</Link> or <Link to="/register">register</Link> to book a room.</p>
        </>
      )}

      <p>
        View our <Link to="/rooms">available rooms</Link>.
      </p>
    </div>
  );
}

export default Home;
